import React, { Component, useState } from 'react'

class Edit extends Component{
    constructor(){
        super();
        this.state ={
            barang : []
        }
    }

    componentDidMount(){
        const {params} = this.props.match;
        fetch('/api/edit/'+params.nama)
        .then(res => res.json())
        .then(barang => this.setState({barang},() => console.log(params.nama,barang)));
    }

    render(){
        const {params} = this.props.match;
        const [nama,setNama] = useState("")
        const changenama = (event)=>{
            setNama(event.currentTarget.value)
        }
        return(
            <div style={{maxWidth:"700px",margin:'4%'}}>
                <div style={{textAlign:"center",marginBottom:"2rem"}}>
                    <h2>Edit Data Product</h2>
                </div>
                {this.state.barang.map(barang => 
                <form action={"/edit/"+params.nama}method="POST" encType="multipart/form-data">
                    <label>Foto Product</label><br/>
                    <input type="file" name="gambar"></input>
                    <br/><br/>
                    <label>Nama Product</label><br/>
                    <input type="text" name="nama" value={nama} onChange={changenama}></input>
                    <br/><br/>
                    <label>Harga Product (IDR)</label><br/>
                    <input type="number" name="harga" placeholder={barang.harga}></input>
                    <br/><br/>
                    <label>Deskripsi Product</label><br/>
                    <textarea type="text" name="deskripsi" placeholder={barang.deskripsi}></textarea>
                    <br/><br/>
                    <label>Stok  Product</label><br/>
                    <input type="number" name="stok" placeholder={barang.stok}></input>
                    <br/><br/>
                    <label>Ukuran Product</label><br/>
                    <input type="text" name="ukuran" placeholder={barang.ukuran}></input>
                    <br/><br/>
                    <button>Simpan</button>
                </form>
                 )}
            </div>
        )
    }
}
export default Edit
