import React, { Component } from 'react';
import '../assets/css//App.css';
import { Route, BrowserRouter as Router} from 'react-router-dom';
import Footer from './footer';
import Home from './home';
import Product from './product'
import Keranjang from './keranjang';
import Upload from '../view/upload/upload';
import Login from '../view/login/login';
import Adduser from '../view/login/adduser';

class App extends Component{

    LoginComponent(){
        return <text>aldi</text>;
    };

    render(){
        return (
            <>
            <div className="header white-black" id="home">
                <div className="header-container">
                    <div className="header-logo">
                        <a href="<?php echo base_url() ?>">CLOTHING STORE {localStorage.getItem("username")}</a>
                    </div>
                    <div className="header-nav">
                        <ul>
                            <li><a href="/">HOME</a></li>
                            <li><a href="/upload">tambah</a></li>
                            <li><a href="/keranjang">KERANJANG</a></li>
                            <li><a href="/login">{this.LoginComponent()}</a></li>
                            <li><a href="/" onClick={event => {
                                localStorage.clear();
                            }}>LOGOUT</a></li>
                        </ul>
                    </div>
                </div>
            </div>
            <div>
            <Router>
                <Route exact path="/" component={Home} />
                <Route exact path='/product' component={Product}/>
                <Route path='/product/:nama' component={Product}/>
                <Route exact path="/keranjang" component={Keranjang} />
                <Route exact path="/upload" component={Upload} />
                <Route exact path="/login" component={Login}/>
                <Route exact path="/adduser" component={Adduser}/>
            </Router>
            </div>
            <Footer />
            </>
        );
    }
}

export default App;
