import React, { useState } from 'react'
import FileUpload from './gambar'

function Upload(){
    const [namaValue,setnamaValue] = useState("")
    const [deskripsiValue,setdeskripsiValue] = useState("")
    const [stokValue,setstokValue] = useState(0)
    const [hargaValue,sethargaValue] = useState(0)

    const namaChange = (event) =>{
        setnamaValue(event.currentTarget.value)
    }

    const deskripsiChange = (event) =>{
        setdeskripsiValue(event.currentTarget.value)
    }

    const stokChange = (event) =>{
        setstokValue(event.currentTarget.value)
    }

    const hargaChange = (event) =>{
        sethargaValue(event.currentTarget.value)
    }

    return(
        
        <div style={{maxWidth:"700px",margin:'4%'}}>
            <div style={{textAlign:"center",marginBottom:"2rem"}}>
                <h2>Upload Product</h2>
            </div>
            <form onSubmit>
                <label>Nama Product</label><br/>
                <input
                    onChange={namaChange}
                    value={namaValue}
                ></input>
                <br/><br/>
                <label>Harga Product (IDR)</label><br/>
                <input
                    onChange={hargaChange}
                    value={hargaValue}
                    type="number"
                ></input>
                <br/><br/>
                <label>Deskripsi Product</label><br/>
                <textarea
                    onChange={deskripsiChange}
                    value={deskripsiValue}
                ></textarea>
                <br/><br/>
                <label>Stok  Product</label><br/>
                <input
                    onChange={stokChange}
                    value={stokValue}
                    type="number"
                ></input>
                <br/><br/>
                <button
                    onClick
                >Simpan</button>
            </form>
        </div>
    )
}
export default Upload
