import React, { Component } from 'react'
import '../assets/css/keranjang.css'

class Keranjang extends Component{
    constructor(){
        super();
        this.state ={
            isikeranjang : []
        }
    }
    componentDidMount(){
        fetch('/api/barang/'+localStorage.getItem("username"))
        .then(res => res.json())
        .then(isikeranjang => this.setState({isikeranjang},() => console.log(isikeranjang)));
    }

    render(){
        return(
            <>
            <h1>ini keranjang</h1>
            <div className="container-product">
                    {this.state.isikeranjang.map(isikeranjang => 
                        <div key = {isikeranjang._id} className="item">
                            <img className="image" src={require('../assets/gambar/'+isikeranjang.gambar)} alt="gambar barang"/>
                            <h4 className="nama">{isikeranjang.nama}</h4>
                            <h5 className="harga">Rp.{isikeranjang.harga}</h5>        
                            <a className="detail" href={'/detail/'+isikeranjang.kategori+"/"+isikeranjang.nama}>Detail</a>
                            <form action="/addkeranjang" method="POST">
                                <input name="username" value={localStorage.getItem("username")} hidden></input>
                                <input name="nama" value={isikeranjang.nama} hidden></input>
                                <button className="beli">Beli</button>
                            </form>
                            
                        </div>
                    )}       
            </div>
            </>
        );
    }
}

export default Keranjang;
