import React, { Component } from 'react'
import '../assets/css/keranjang.css'

class Keranjang extends Component{
    constructor(){
        super();
        this.state ={
            barang : [],
            keranjang : []
        }
    }

    componentDidMount(){
        fetch('/api/keranjang/'+localStorage.getItem("username"))
        .then(res => res.json())
        .then(barang => this.setState({barang},() => console.log(barang)));
    }

    render(){
        return(
            <div className="container-keranjang">
                   {this.state.barang.map(barang => 
                        <div key = {barang._id} className="item-keranjang">
                            <table border="3">
                                    <tr>
                                        <th style={{width:"20%"}}>Nama</th>
                                        <th style={{width:"25%"}}>Gambar</th>
                                        <th style={{width:"15%"}}>Harga</th>
                                        <th style={{width:"15%"}}>Jumlah</th>
                                        <th style={{width:"15%"}}>Total</th>
                                        <th style={{width:"10%"}}>Aksi</th>
                                    </tr>
                                    <tr>
                                        <td>{barang.nama}</td>
                                        <td><img style={{width:"15%",height:"15%"}} src={require('../assets/gambar/'+barang.gambar)} alt="gambar barang"></img></td>
                                        <td>{barang.harga}</td>
                                        <td>{barang.jumlah}</td>
                                        <td>{barang.total}</td>
                                    </tr>
                            </table>
                        </div>
                    )}       
            </div>
        );
    }
}

export default Keranjang;
